mapper.select:
SELECT id, order_id as orderId, shop_id as shopId, goods_id as goodsId, current_price as currentPrice, quantity, total_price as totalPrice, create_time as createTime, update_time as updateTime FROM `order_detail`
id = #{id}
and order_id = #{orderId}
and shop_id = #{shopId}
and goods_id = #{goodsId}

SELECT d.id AS orderId, a.`status`, d.current_price as currentPrice, d.quantity, d.total_price as totalPrice, d.create_time as createTime, e.`name`, b.username, c.address, c.mobil FROM `order` a LEFT JOIN USER b ON a.user_id = b.id LEFT JOIN address c ON a.user_addres = c.id LEFT JOIN `order_detail` d ON d.order_id = a.id LEFT JOIN goods e ON d.goods_id = e.id
d.shop_id = #{shopId}
and date_format(d.create_time,'%Y-%m-%d') between date_format(#{startTime},'%Y-%m-%d') and date_format(#{endTime},'%Y-%m-%d')

SELECT d.main_images as imgUrl, d.`name` as goodsName, a.id as orderId, a.payment as payment, a.`status`, e.shop_name as shopName, b.current_price as currentPrice, b.quantity as quantity, b.total_price as totalPrice, c.address as address, a.end_time AS createTime, b.create_time as payTime, a.user_id as userId FROM `order` a LEFT JOIN order_detail b ON a.id = b.order_id LEFT JOIN address c ON a.user_addres = c.id LEFT JOIN goods d ON b.goods_id = d.id LEFT JOIN shop e ON b.shop_id = e.shop_id
a.user_id = #{userId}
and a.id = #{orderId}
order BY a.id DESC
